library(shiny) library(ggplot2)
ui &lt;- fluidPage( titlePanel(“Diamonds Explorer”), sidebarLayout( sidebarPanel( sliderInput(‘sampleSize’, ‘Sample Size’, min = 1, max = nrow(diamonds), value = min(1000, nrow(diamonds)), step = 500, round = 0), br(), checkboxInput(‘jitter’, ‘Jitter’), checkboxInput(‘smooth’, ‘Smooth’), selectInput(‘x’, ‘X’, names(diamonds)), selectInput(‘y’, ‘Y’, names(diamonds), names(diamonds)[[2]]), selectInput(‘color’, ‘Color’, c(‘None’, names(diamonds))), selectInput(‘facet_row’, ‘Facet Row’, c(None = ‘.’, names(diamonds[sapply(diamonds, is.factor)]))), selectInput(‘facet_col’, ‘Facet Column’, c(None = ‘.’, names(diamonds[sapply(diamonds, is.factor)]))) ), mainPanel( plotOutput(‘plot’), tableOutput(‘data’) ) ) )
server &lt;- function(input, output) { dataset &lt;- reactive({ diamonds[sample(nrow(diamonds), input$sampleSize), ] })
output\(plot &lt;- renderPlot({
    p &lt;- ggplot(
      dataset(),
      aes_string(x = input\)x, y = input$y)) + geom_point()
if (input$color != 'None')
  p &lt;- p + aes_string(color = input$color)

facets &lt;- paste(input$facet_row, '~', input$facet_col)
if (facets != '. ~ .')
  p &lt;- p + facet_grid(facets)

if (input$jitter)
  p &lt;- p + geom_jitter()
if (input$smooth)
  p &lt;- p + geom_smooth()

p
})
output$data &lt;- renderTable({ dataset() }) }
shinyApp(ui = ui, server = server)
